  const [activeTab, setActiveTab] = useState('1');

  // 准备雷达图数据
  const radarData = {
    labels: [
      '情感安全感',
      '个人空间',
      '共同成长',
      '现实务实',
      '情绪表达'
    ],
    datasets: [
      {
        label: '你的情感画像',
        data: [
          result.dimensions.S,
          result.dimensions.A,
          result.dimensions.G,
          result.dimensions.R,
          result.dimensions.E
        ],
        fill: true,
        backgroundColor: 'rgba(102, 126, 234, 0.2)',
        borderColor: 'rgb(102, 126, 234)',
        pointBackgroundColor: 'rgb(102, 126, 234)',
        pointBorderColor: '#fff',
        pointHoverBackgroundColor: '#fff',
        pointHoverBorderColor: 'rgb(102, 126, 234)'
      },
      {
        label: '理想伴侣',
        data: [
          result.idealPartner.baseType.idealProfile.S,
          result.idealPartner.baseType.idealProfile.A,
          result.idealPartner.baseType.idealProfile.G,
          result.idealPartner.baseType.idealProfile.R,
          result.idealPartner.baseType.idealProfile.E
        ],
        fill: true,
        backgroundColor: 'rgba(82, 196, 26, 0.2)',
        borderColor: 'rgb(82, 196, 26)',
        pointBackgroundColor: 'rgb(82, 196, 26)',
        pointBorderColor: '#fff',
        pointHoverBackgroundColor: '#fff',
        pointHoverBorderColor: 'rgb(82, 196, 26)'
      }
    ]
  };

  const radarOptions = {
    responsive: true,
    maintainAspectRatio: true,
    scales: {
      r: {
        beginAtZero: true,
        max: 100,
        ticks: {
          stepSize: 20
        }
      }
    },
    plugins: {
      legend: {
        display: true,
        position: 'bottom' as const
      },
      tooltip: {
        callbacks: {
          label: function(context: any) {
            return `${context.dataset.label}: ${context.parsed.r}分`;
          }
        }
      }
    }
  };

  const getDimensionColor = (level: string) => {
    switch (level) {
      case '极高': return '#52c41a';
      case '较高': return '#73d13d';
      case '中等': return '#faad14';
      case '较低': return '#ff7a45';
      case '很低': return '#ff4d4f';
      default: return '#d9d9d9';
    }
  };

  const getDimensionName = (key: keyof typeof result.dimensions) => {
    const names = {
      'S': '情感安全感需求',
      'A': '个人空间需求',
      'G': '共同成长重视度',
      'R': '现实务实程度',
      'E': '情绪表达偏好'
    };
    return names[key];
  };

  // 格式化特质文本，分离标题和描述
  const formatTrait = (trait: string) => {
    const colonIndex = trait.indexOf('：');
    if (colonIndex > -1) {
      return {
        title: trait.substring(0, colonIndex),
        description: trait.substring(colonIndex + 1)
      };
    }
    return {
      title: trait,
      description: ''
    };
  };

  // 生成个性化的匹配原因解释
  const generatePersonalizedReason = (result: TestResult) => {
    const { dimensions, mainType, emotionalType } = result;

    // 根据用户的五维度得分生成特征描述
    const getUserCharacteristics = () => {
      const characteristics = [];

      if (dimensions.S >= 70) {
        characteristics.push('非常重视情感安全感，渴望稳定和被珍视的关系');
      } else if (dimensions.S >= 50) {
        characteristics.push('需要适度的情感安全感，重视关系的稳定性');
      } else {
        characteristics.push('相对独立，对情感依赖度较低');
      }

      if (dimensions.A >= 70) {
        characteristics.push('非常重视个人空间和独立性');
      } else if (dimensions.A >= 50) {
        characteristics.push('在亲密关系中仍需要一定的个人空间');
      } else {
        characteristics.push('愿意与伴侣分享大部分生活，不太介意个人边界');
      }

      if (dimensions.G >= 70) {
        characteristics.push('有强烈的事业心和成长追求，希望与伴侣共同进步');
      } else if (dimensions.G >= 50) {
        characteristics.push('重视个人成长，希望伴侣能够支持自己的发展');
      } else {
        characteristics.push('更注重当下的情感体验，对事业成就要求不高');
      }

      if (dimensions.R >= 70) {
        characteristics.push('非常务实和理性，重视实际的生活保障');
      } else if (dimensions.R >= 50) {
        characteristics.push('在感性和理性之间保持平衡');
      } else {
        characteristics.push('更倾向于感性和理想化的关系');
      }

      if (dimensions.E >= 70) {
        characteristics.push('情感丰富细腻，需要深度的情感交流和共鸣');
      } else if (dimensions.E >= 50) {
        characteristics.push('重视情感表达，但也需要理性的平衡');
      } else {
        characteristics.push('更倾向于用行动表达情感，不太善于情感表达');
      }

      return characteristics;
    };

    const userCharacteristics = getUserCharacteristics();

    // 根据伴侣类型生成匹配解释
